import React, {useContext, useState } from 'react'
import {CustomerContext} from '../context/CustomerContext'
import {Container,Table} from 'react-bootstrap'
import axios from 'axios'
const Customers = () => {
    const [customer, setCustomer] = useContext(CustomerContext)
    const [approve, setApprove] = useState(false)

    const updateCustomer = (id) => {
        console.log(id)
         axios.patch('/customers/'+ id, { approved: setApprove})
              .then(res => { 
                  console.log(res)
              })
              .catch(err => console.log(err))

     }

     //when the check box is toggled
    const handleCheckboxChange = (docid) =>{
        console.log(docid)
        setApprove(!approve)
        axios.patch('/customers/'+ docid, {status: true})
        .then(res => { 
            console.log(res)
        })
        .catch(err => console.log(err))
        // customer.forEach(element => {
        //     if(element.id === docid){
        //         console.log(element.approved);
        //         console.log([customer])
        //     }

        //);
        //setCustomer(PrevCustomer => [...PrevProduct.filter(product => product.id === id)])
        //setCustomer(PrevCustomer => [ customer[0].approved = !approved])
        //setCustomer(PrevProduct => [customer[0].approved = true,{}...PrevProduct.filter(product => product.id === id)]);

     }
    return (
        <Container className="container" id="dataDiv" fluid>
            <Table Table striped hover className="mb-0">
                <thead>
                    <tr>
                        <th>UUID</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Address</th>
                        <th>Approved</th>
                        <th>Action</th>
                    </tr>
                </thead>
                {customer.map(({_id, name, email, address, approved}) => (
                    <tbody key={_id}>
                        <tr> 
                            <td>
                                {_id}
                            </td>
                            <td>
                                {name}
                            </td>
                            <td>
                                {email}
                            </td>
                            <td>
                                {address}
                            </td>
                            <td>
                                {approved.toString()}
                            </td>
                            <td>
                                <input type="checkbox"  defaultChecked={approved} onChange={() => handleCheckboxChange(_id)} />
                            </td>
                        </tr>
                    </tbody>                                
                ))}
            </Table>
        </Container>
    )
}
export default Customers;
