import React, { Component } from 'react'
import {Container,Button, Table} from 'react-bootstrap';
import {v1 as uuid} from "uuid"; 
class viewProducts extends Component {
    constructor(props){
        super(props)
        this.state = {
            products:[
                {id: uuid(), name: 'Water', price: 7, description: "water descrip"},
                {id: uuid(), name: 'Milk', price: 17, description: "milk descrip"},
                {id: uuid(), name: 'Soap', price: 6, description: "soap descrip"},
                {id: uuid(), name: 'Rice', price: 3, description: "rice descrip"},
            ]
        }
    }
    render() {
        const {products} = this.state;
        return (
            <Container className="p-0" fluid>
                 <Table Table striped hover className="mb-0">
                     <tr> 
                         <thead> Product Name</thead>
                         <thead> Product Price</thead>
                         <thead> Product Description</thead>
                     </tr>
                    {products.map(({id, name, price, description, i}) => (
                        <tbody>
                            <tr key={i}> 
                                <td>
                                    {name}
                                </td>
                                <td>
                                    {price}
                                </td>
                                <td>
                                    {description}
                                </td>
                                <td>
                                    <Button variant="outline-primary" size="sm" onClick={() => { 
                                    console.log( "enter new item")
                                    }}>
                                        Edit
                                    </Button>
                                </td>
                                <td>
                                    <Button variant="outline-danger" size="sm" onClick={() => { 
                                        this.setState( {products: this.state.products.filter(product => product.id !==id )
                                        })
                                    }}>
                                        Remove
                                    </Button>
                                </td>
                            </tr>
                        </tbody>                                
                    ))}
                  </Table>
            </Container>
        )
    }
}
export default viewProducts;
