import React, { Component } from 'react';
import {v1 as uuid} from "uuid"; 
import { Button} from 'react-bootstrap';
class addProduct extends Component {
    constructor(props){
        super(props)
        this.state = {
            name: "",
            price: "",
            description: "",
            products:[
                {id: uuid(), name: 'Water', price: 7, description:""},
                {id: uuid(), name: 'Milk', price: 17, description:""},
                {id: uuid(), name: 'Soap', price: 6, description:""},
                {id: uuid(), name: 'Rice', price: 3, description:""},
            ]
        }
    }
    onSubmit = (e) => {
        e.preventDefault();
        this.props.addProduct(this.state.name)
        this.setState({name:'', price:'', description: ''})
    }
    render() {
        const {products} = this.state;
        return (
            <form onSubmit={this.onSubmit} className="container">
                    <div className="row">
                        <div className="col-sm">
                            <input type="text" className="form-control mb-2 mr-sm-2" id="name" placeholder="Enter Product Name" name="name"  value={this.state.name} onChange={(e) => this.setState({name: e.target.value})}/>
                        </div>
                        <div className="col-sm">
                            <input type="text" className="form-control mb-2 mr-sm-2" id="price" placeholder="Enter Product Price" name="price" value={this.state.price} onChange={(e) => this.setState({price: e.target.value})}/>
                        </div>
                        <div className="col-sm">
                            <input type="text" className="form-control mb-2 mr-sm-2" id="price" placeholder="Enter Product Description" name="description"  value={this.state.description} onChange={(e) => this.setState({description: e.target.value})} />
                        </div>
                        <div className="col-sm">
                            <input type="submit" value="Add" variant="outline-primary" size="lg" onClick={() => {
                               // this.setState({products: [...this.state.products, {id: uuid(), name, price, description }]})
                                // this.setState(this.state, ({
                                //      products: [...state.products, {id: uuid(), name, price, description }]
                                //     }))

                                }}>
                            </input>
                        </div>
                    </div>
                </form>

        )
    }
}

export default addProduct
