import React, { Component } from 'react'
import {ListGroup, ListGroupItem,Button, Table} from 'react-bootstrap';
import {v1 as uuid} from "uuid"; 
class viewProducts extends Component {
    constructor(props){
        super(props)
        this.state = {
            products:[
                {id: uuid(), name: 'Water', price: 7, description: "dknjchfgfhj"},
                {id: uuid(), name: 'Milk', price: 17, description: "dknjchfgfhj"},
                {id: uuid(), name: 'Soap', price: 6, description: "dknjchfgfhj"},
                {id: uuid(), name: 'Rice', price: 3, description: "dknjchfgfhj"},
            ]
        }
    }
    render() {
        const {products} = this.state;
        return (
            <div>
                <ListGroup striped hover className="mb-0">
                    {products.map(({id, name, price, description}) => (
                        <Table>
                            <td>
                                {name}
                            </td>
                            <td>
                                {price}
                            </td>
                            <td>
                                {description}
                            </td>
                            <Button variant="outline-danger" size="sm" onClick={() => { 
                                this.setState( {products: this.state.products.filter(product => product.id !==id )
                                })
                            }}>
                                Remove
                            </Button>
                        </Table>
                        // <tr  onClick={() => console.log(price)}>             
                        //     <td>{name}</td>
                        //     <td>{price}</td>
                        // </tr>

                    ))}
                </ListGroup>
            </div>
        )
    }
}
export default viewProducts;
