import React from 'react'
import { Route, Redirect } from 'react-router-dom'

const  protectedRoute = ({component:Component, ...rest})  => {
    const isAuth = false;
    return (
       <Route {...rest} render={props => {
           if(isAuth){
                return <Component {...props}/>
           }else{
                return <Redirect to={
                    {
                        pathname:"/",
                        state:{
                            from: props.location
                        }
                    }
                }/>
           }
       }}
       />
    )
}

export default protectedRoute
