import React, { useContext, useState } from 'react'
import {ProductContext} from '../context/ProductContext'
import {Container,Button, Table} from 'react-bootstrap'
import AddProduct from '../components/AddProduct';
import EditProduct from '../components/EditProduct';
import Moment from 'react-moment';
import axios from 'axios'
const Products = () => {
   const [productInfo, setProductInfo] = useState({})
   const [name, setName] = useState('');
   const [price, setPrice] = useState('');
   const [description, setDescription] = useState('');

   const [Editiing, SetEditing] = useState(false)
   const [pID, setPID] = useState('')
   const [product, setProduct] = useContext(ProductContext)

   const deleteProduct = (id) => {
       axios.delete('/products/'+id)
            .then(res => {
                console.log(res)
            })
            .catch(err =>{ 
                console.error(err);
        })
    }
    const SaveProduct = (id) => {
        console.log(id)
        SetEditing(false);
        axios.path('/products/'+id, {name,price,description}) //TODO to call function from context
            .then(res => {
            console.log(res)
            })
            .catch(err =>{ 
                console.error(err);
        })
        //  axios.patch('')
        //       .then(res => this.setState( {products: this.state.products.filter(product => product.id !==id )}) )
        //       .catch(err => console.log(err))
        //  setProduct(PrevProduct => [...PrevProduct.filter(product => product.id !== id)]);
     }
    const EditProduct = (id) => {
        console.log(id)
        setPID(id)
        SetEditing(Editiing);
         setProduct(PrevProduct => [...PrevProduct.filter(product => product.id !== id)]);
     }
    return (
        <Container className="container" id="dataDiv" fluid>
             <div>
                <AddProduct />
             </div>
             <div className="editDiv">
                <form onSubmit={SaveProduct} className="container">
                    <div className="row">
                        <div className="col-sm">
                            <input type="text" className="form-control mb-2 mr-sm-2" id="name" placeholder="Enter Product Name" name="name"  value={name} onChange={(e) => setName(e.target.value)} required/>
                        </div>
                        <div className="col-sm">
                            <input type="number" min="1" className="form-control mb-2 mr-sm-2" id="price" placeholder="Enter Product Price" name="price" value={price} onChange={(e) => setPrice(e.target.value)} required/>
                        </div>
                        <div className="col-sm">
                            <input type="text" className="form-control mb-2 mr-sm-2" id="price" placeholder="Enter Product Description" name="description"  value={description} onChange={(e) => setDescription(e.target.value)} required/>
                        </div>
                        <div className="col-sm">
                            <Button variant="outline-primary" size="lg" type="submit">
                                Save
                            </Button>
                        </div>
                    </div>
                </form>
             </div>
            <Table Table striped hover className="mb-0">
                <thead>
                    <tr>
                        <th>ProductName</th>
                        <th>ProductPrice (CAD)</th>
                        <th>ProductDescription</th>
                        <th>ProductCreatedDate</th>
                        <th>Update</th>
                        <th>Delete</th>
                    </tr>
                </thead>
                {product.map(({_id, productName, ProductPrice, ProductDescription, ProductCreatedDate}) => (
                    <tbody key={_id}>
                        <tr> 
                            <td>
                                {/* <input value={name} onChange={(e) => setName(e.target.value)}/> */}
                                {productName}
                            </td>
                            <td>
                                    {/* <input value ={price} onChange={(e) => setPricee(e.target.price)}/> */}
                                {ProductPrice}
                            </td>
                            <td>
                                    {/* <input value ={description}  onChange={(e) => setDecription(e.target.value)} /> */}
                                {ProductDescription}
                            </td>
                            <td>
                                    {/* <input value ={description}  onChange={(e) => setDecription(e.target.value)} /> */}
                                    <Moment format="YYYY-MM-DD">{ProductCreatedDate}</Moment> 
                            </td>
                            <td>
                                <Button variant="outline-primary" size="sm" onClick={() => EditProduct(_id)}>

                                    Edit
                                </Button>
                            </td>
                            <td>
                                <Button variant="outline-danger" size="sm" onClick={() => deleteProduct(_id)}>
                                    Remove
                                </Button>
                            </td>
                        </tr>
                    </tbody>                                
                ))}
            </Table>
        </Container>

    )
}

export default Products;
