import React, { useState, useContext } from 'react'
import {ProductContext} from '../context/ProductContext'
import {Container,Button, Table} from 'react-bootstrap'
import axios from 'axios'

const Products = () => {
    console.log(ProductContext)
    const [product, setProduct] = useContext(ProductContext)
    console.log(product)
   const deleteProduct = (id) => {
        axios.delete('')
             .then(res => this.setState( {products: this.state.products.filter(product => product.id !==id )}) )
             .catch(err => console.log(err))
        this.setState( {products: this.state.products.filter(product => product.id !==id )})
    }
    return (
        <div>
            <Container className="p-0" fluid>
                 {/* <AddProduct addProduct={this.addProduct} /> */}
                 <Table Table striped hover className="mb-0">

                    {product.map(({id, name, price, description}) => (
                        <tbody key={id}>
                            <tr> 
                                <td>
                                    {name}
                                </td>
                                <td>
                                    {price}
                                </td>
                                <td>
                                    {description}
                                </td>
                                <td>
                                    <Button variant="outline-primary" size="sm" onClick={() => { 
                                    console.log( "enter new item")
                                    }}>
                                        Edit
                                    </Button>
                                </td>
                                <td>
                                    <Button variant="outline-danger" size="sm" onClick={(e) => {deleteProduct}}>
                                        Remove
                                    </Button>
                                </td>
                            </tr>
                        </tbody>                                
                    ))}
                  </Table>
            </Container>
        </div>
    )
}

export default Products;
