        this.state = {
            products:[
                {id: uuid(), name: 'Water', price: 7},
                {id: uuid(), name: 'Milk', price: 17},
                {id: uuid(), name: 'Soap', price: 6},
                {id: uuid(), name: 'Rice', price: 3},
            ]
        }
    }
    render() {
        const {products} = this.state;
        return (
            <div className="container">
                    <div className="row">
                        <div class="col-sm">
                            <input type="text" class="form-control mb-2 mr-sm-2" id="name" placeholder="Enter Product Name" name="name" />
                        </div>
                        <div class="col-sm">
                            <input type="text" class="form-control mb-2 mr-sm-2" id="price" placeholder="Enter Product Price" name="price" />
                        </div>
                        <div class="col-sm">
                            <input type="text" class="form-control mb-2 mr-sm-2" id="price" placeholder="Enter Product Description" name="description" />
                        </div>
                        <div class="col-sm">
                            <Button variant="outline-primary" size="lg" onClick={() => {

                            }}>
                                    Add
                            </Button>
                        </div>
                    </div>
                </div>

        )
    }
}

export default addProduct
